	////////////////////////////////////////////////////////////////////////
	//
	// Peripheral address decoding, bus handling
	// {{{
	//
	// BUS-LOGIC for wbwide
	// {{{
	//
	// No class SINGLE peripherals on the "wbwide" bus
	//

	//
	// No class DOUBLE peripherals on the "wbwide" bus
	//

	// info: @ERROR.WIRE for wbdown matches the buses error name, wbwide_wbdown_err
	assign	wbwide_bkram_err= 1'b0;
	assign	wbwide_ddr3_controller_err= 1'b0;
	//
	// Connect the wbwide bus components together using the wbxbar()
	//
	//
	wbxbar #(
		.NM(7), .NS(3), .AW(25), .DW(512),
		.SLAVE_ADDR({
			// Address width    = 25
			// Address LSBs     = 6
			{ 25'h1000000 }, // ddr3_controller: 0x40000000
			{ 25'h0100000 }, //           bkram: 0x04000000
			{ 25'h0080000 }  //          wbdown: 0x02000000
		}),
		.SLAVE_MASK({
			// Address width    = 25
			// Address LSBs     = 6
			{ 25'h1000000 }, // ddr3_controller
			{ 25'h1f80000 }, //           bkram
			{ 25'h1f80000 }  //          wbdown
		}),
		.OPT_DBLBUFFER(1'b1))
	wbwide_xbar(
		.i_clk(i_clk), .i_reset(i_reset),
		.i_mcyc({
			wbwide_cpunetm_cyc,
			wbwide_gnet_cyc,
			wbwide_hdmi_cyc,
			wbwide_zip_cyc,
			wbwide_wbu_arbiter_cyc,
			wbwide_i2cdma_cyc,
			wbwide_i2cm_cyc
		}),
		.i_mstb({
			wbwide_cpunetm_stb,
			wbwide_gnet_stb,
			wbwide_hdmi_stb,
			wbwide_zip_stb,
			wbwide_wbu_arbiter_stb,
			wbwide_i2cdma_stb,
			wbwide_i2cm_stb
		}),
		.i_mwe({
			wbwide_cpunetm_we,
			wbwide_gnet_we,
			wbwide_hdmi_we,
			wbwide_zip_we,
			wbwide_wbu_arbiter_we,
			wbwide_i2cdma_we,
			wbwide_i2cm_we
		}),
		.i_maddr({
			wbwide_cpunetm_addr,
			wbwide_gnet_addr,
			wbwide_hdmi_addr,
			wbwide_zip_addr,
			wbwide_wbu_arbiter_addr,
			wbwide_i2cdma_addr,
			wbwide_i2cm_addr
		}),
		.i_mdata({
			wbwide_cpunetm_data,
			wbwide_gnet_data,
			wbwide_hdmi_data,
			wbwide_zip_data,
			wbwide_wbu_arbiter_data,
			wbwide_i2cdma_data,
			wbwide_i2cm_data
		}),
		.i_msel({
			wbwide_cpunetm_sel,
			wbwide_gnet_sel,
			wbwide_hdmi_sel,
			wbwide_zip_sel,
			wbwide_wbu_arbiter_sel,
			wbwide_i2cdma_sel,
			wbwide_i2cm_sel
		}),
		.o_mstall({
			wbwide_cpunetm_stall,
			wbwide_gnet_stall,
			wbwide_hdmi_stall,
			wbwide_zip_stall,
			wbwide_wbu_arbiter_stall,
			wbwide_i2cdma_stall,
			wbwide_i2cm_stall
		}),
		.o_mack({
			wbwide_cpunetm_ack,
			wbwide_gnet_ack,
			wbwide_hdmi_ack,
			wbwide_zip_ack,
			wbwide_wbu_arbiter_ack,
			wbwide_i2cdma_ack,
			wbwide_i2cm_ack
		}),
		.o_mdata({
			wbwide_cpunetm_idata,
			wbwide_gnet_idata,
			wbwide_hdmi_idata,
			wbwide_zip_idata,
			wbwide_wbu_arbiter_idata,
			wbwide_i2cdma_idata,
			wbwide_i2cm_idata
		}),
		.o_merr({
			wbwide_cpunetm_err,
			wbwide_gnet_err,
			wbwide_hdmi_err,
			wbwide_zip_err,
			wbwide_wbu_arbiter_err,
			wbwide_i2cdma_err,
			wbwide_i2cm_err
		}),
		// Slave connections
		.o_scyc({
			wbwide_ddr3_controller_cyc,
			wbwide_bkram_cyc,
			wbwide_wbdown_cyc
		}),
		.o_sstb({
