		// }}}
	);

	assign	edid_valid = i2c_valid && (i2c_id == 1);
	assign	edid_data  = i2c_data;
	assign	edid_last  = i2c_last;
	// }}}
	// }}}
`else	// EDID_ACCESS
	// {{{
	assign	o_edid_scl = 1'b1;
	assign	o_edid_sda = 1'b1;
	// }}}
`endif	// EDID_ACCESS

