	//
	// No class DOUBLE peripherals on the "wbwide" bus
	//

	// info: @ERROR.WIRE for wbdown matches the buses error name, wbwide_wbdown_err
`ifdef	BKRAM_ACCESS
	assign	wbwide_bkram_err= 1'b0;
`endif	// BKRAM_ACCESS
	// info: @ERROR.WIRE for ddr3_controller matches the buses error name, wbwide_ddr3_controller_err
	//
	// Connect the wbwide bus components together using the wbxbar()
	//
	//
	wbxbar #(
		.NM(5), .NS(3), .AW(25), .DW(512),
		.SLAVE_ADDR({
			// Address width    = 25
			// Address LSBs     = 6
			{ 25'h1000000 }, // ddr3_controller: 0x40000000
			{ 25'h0100000 }, //           bkram: 0x04000000
			{ 25'h0080000 }  //          wbdown: 0x02000000
		}),
		.SLAVE_MASK({
			// Address width    = 25
			// Address LSBs     = 6
			{ 25'h1000000 }, // ddr3_controller
			{ 25'h1f80000 }, //           bkram
			{ 25'h1f80000 }  //          wbdown
		}),
		.OPT_DBLBUFFER(1'b1)
	) wbwide_xbar(
		.i_clk(i_clk), .i_reset(i_reset),
		.i_mcyc({
			wbwide_sata_cyc,
			wbwide_i2cm_cyc,
			wbwide_i2cdma_cyc,
			wbwide_zip_cyc,
			wbwide_wbu_arbiter_cyc
		}),
		.i_mstb({
			wbwide_sata_stb,
			wbwide_i2cm_stb,
			wbwide_i2cdma_stb,
			wbwide_zip_stb,
			wbwide_wbu_arbiter_stb
		}),
		.i_mwe({
			wbwide_sata_we,
			wbwide_i2cm_we,
			wbwide_i2cdma_we,
			wbwide_zip_we,
			wbwide_wbu_arbiter_we
		}),
		.i_maddr({
			wbwide_sata_addr,
			wbwide_i2cm_addr,
			wbwide_i2cdma_addr,
			wbwide_zip_addr,
			wbwide_wbu_arbiter_addr
		}),
		.i_mdata({
			wbwide_sata_data,
			wbwide_i2cm_data,
			wbwide_i2cdma_data,
			wbwide_zip_data,
			wbwide_wbu_arbiter_data
		}),
		.i_msel({
			wbwide_sata_sel,
			wbwide_i2cm_sel,
			wbwide_i2cdma_sel,
			wbwide_zip_sel,
			wbwide_wbu_arbiter_sel
		}),
		.o_mstall({
			wbwide_sata_stall,
			wbwide_i2cm_stall,
			wbwide_i2cdma_stall,
			wbwide_zip_stall,
			wbwide_wbu_arbiter_stall
		}),
		.o_mack({
			wbwide_sata_ack,
			wbwide_i2cm_ack,
			wbwide_i2cdma_ack,
